mapper.select:
select
  id, anonymity, customer_id, customer_image, customer_name, ordering_id, store_id, date, score, evaluation, image1, image2, image3, image4
from eattingwhat2.evaluation


        and id = #{id}


        and anonymity = #{anonymity}


        and customer_id = #{customerId}


        and customer_image = #{customerImage}


        and customer_name = #{customerName}


        and ordering_id = #{orderingId}


        and store_id = #{storeId}


        and date = #{date}


        and score = #{score}


        and evaluation = #{evaluation}


        and image1 = #{image1}


        and image2 = #{image2}


        and image3 = #{image3}


        and image4 = #{image4}
